import urllib2 
from bs4 import BeautifulSoup
import csv 
import collections as ct

'''
1. Read in cases from database csv 
2. Crawl OPC site to add any that were missed 
3. Add to CSV, pass CSV into crawl_detail_loader 
'''
class Crawler:
	db = 'opc_cases_scraped.csv'

	def read_keys(self, dict_arr=True): 
		data = set()
		with open(self.db) as csvfile:
			reader = csv.DictReader(csvfile)
			for row in reader:
				case_url = row['Case URL']
				data.add(case_url)
		return data

	@staticmethod
	def _extract_case_urls(url, verbose=False):
		html_doc = urllib2.urlopen(url).read()
		soup = BeautifulSoup(html_doc, 'html.parser')
		links = []
		links_text = []
		start_index, end_index = 0, 0
		key = 'How the OPC Enforces PIPEDA'

		for i, link in enumerate(soup.find_all('a')):
			if link.string != None and key in link.string:
				start_index = i + 1 
			
			links.append(link.get('href'))
			links_text.append(link.string)	
			
			if link.string == 'First': 
				end_index = i 
				break 

			if link.string == 'Previous': 
				end_index = i
				break 
			
			if link.string == '1': 
				end_index = i 
				break 

		# if verbose: #sanity check 
		# 	print(links[start_index:end_index])
		# 	print(links_text[start_index:end_index])

		return links[start_index:end_index] 
	
	@staticmethod
	def _extract_case_numbers(url): 
		case_numbers = []
		html_doc = urllib2.urlopen(url).read()
		soup = BeautifulSoup(html_doc, 'html.parser')
		for node in soup.find_all('strong'): 
			if '#' in node.text: 
				case_number = node.text.split('#')[1].strip()
				case_numbers.append('#' + case_number)			
			if 'Letter of finding' in node.text: #edge case 
				case_numbers.append('N/A')
		return case_numbers 

	@staticmethod
	def _extract_case_name(soup): 
		# print(soup.find_all('title'))
		case_title = soup.find_all('title')[0].text
		if ":" in case_title: 
			case_name = case_title.split(':')[1].strip()
		else: 
			case_name = case_title 
		return case_name 

	@staticmethod
	def _extract_last_updated(url):
		dates = []
		html_doc = urllib2.urlopen(url).read()
		soup = BeautifulSoup(html_doc, 'html.parser')
		for node in soup.find_all('strong'):
			if node.time is not None: 
				dates.append(node.time.text)
		return dates
	
	def _extract_case_data(self, case_urls, case_numbers, case_dates, data, verbose=False): 
		base = 'https://www.priv.gc.ca/'
		for i, url in enumerate(case_urls): 
			url = base + url 
			html_doc = urllib2.urlopen(url).read()
			soup = BeautifulSoup(html_doc, 'html.parser')
			if verbose: 
				print("On case " + str(i + 1))
			case_url = url 
			case_name = self._extract_case_name(soup)
			case_number = case_numbers[i]
	
			last_updated = case_dates[i]
			
			data[case_url] = [case_name.encode('utf-8'), case_url.encode('utf-8'), case_number.encode('utf-8'), last_updated.encode('utf-8')]
	
	def _crawl_by_page(self, verbose=False): 
		data = {}
		page_number = 1
		page_url="https://www.priv.gc.ca/en/opc-actions-and-decisions/investigations/investigations-into-businesses/?Page="
		case_count = 0 
		case_total = 618 

		while True: 
			url = page_url
			url += str(page_number) 
			print("On page: %d" % (page_number))
			page_number += 1

			case_urls = self._extract_case_urls(url, verbose)
			case_numbers = self._extract_case_numbers(url)
			case_dates = self._extract_last_updated(url)
			self._extract_case_data(case_urls, case_numbers, case_dates, data, verbose)
			
			case_count += len(case_urls)
			if case_count >= case_total:
				break 
		return data 

	def _write(self, data): 
		with open('additional_opc_cases.csv', 'w') as csvfile: 
			writer = csv.writer(csvfile)
			fieldnames = ['Case Name', 'Case URL', 'Case Number', 'Last Updated']
			writer.writerow(fieldnames)
			for key in data: 
				writer.writerow(data[key])

